using System;
using System.Collections.Generic;
using System.Linq;

internal class Producto
{
    public string Nombre { get; set; }
    public decimal Precio { get; set; }
    public string Categoria { get; set; }
}

internal class Program
{
    static void Main()
    {
        List<Producto> productos = new List<Producto>
        {
            new Producto { Nombre = "Laptop", Precio = 1000, Categoria = "Electronica" },
            new Producto { Nombre = "Telefono", Precio = 500, Categoria = "Electronica" },
            new Producto { Nombre = "Silla", Precio = 150, Categoria = "Muebles" },
            new Producto { Nombre = "Mesa", Precio = 300, Categoria = "Muebles" }
        };

        var agrupar = productos.GroupBy(p => p.Categoria)
         .Select(grupo => new { Categoria = grupo.Key, PrecioPromedio = grupo.Average(p => p.Precio) });

        foreach (var grupo in agrupar)
        {
            Console.WriteLine($"Categoría: {grupo.Categoria}, precio promedio: {grupo.PrecioPromedio}");
        }
    }
}
